
$(document).ready(()=>{$(".goods__card__favourite").click((e)=>{let currentElement=$(e.target);e.preventDefault();if(currentElement.hasClass("active")){currentElement.removeClass("active");}else{currentElement.addClass("active");}});let arrow=$(".top__language-arrow");let top_language=$(".top__language-select");let flag_language=false;$(".top__language").click(()=>{if(!flag_language){arrow.addClass("arrow__active");top_language.show();top_language.removeClass("select-close");top_language.addClass("select-open");flag_language=true;}else{arrow.removeClass("arrow__active");top_language.removeClass("select-open");top_language.addClass("select-close");top_language.hide(200);flag_language=false;}});let menu_catalog=$(".menu-catalog");let menu_catalog_close=$(".menu-catalog__close");let body=$("body");let btn_menu=$(".top__btn");menu_catalog.hide();btn_menu.on("click",()=>{menu_catalog.slideDown(500);body.css("overflow","hidden");});menu_catalog_close.on("click",()=>{menu_catalog.slideUp(500);body.css("overflow","visible");});$(".goods__card__bag").click(()=>{let count=$(".count").text();count++;$(".count").text(count);});$(".goods__cards").slick({infinite:false,arrow:true,slidesToShow:4,slidesToScroll:1,variableWidth:true,infinite:true,});$(".popup-youtube").magnificPopup({disableOn:700,type:"iframe",mainClass:"mfp-fade",removalDelay:160,preloader:false,fixedContentPos:false,});});