
$(document).ready(()=>{$(".goods__card__favourite").click((e)=>{let currentElement=$(e.target);e.preventDefault();if(currentElement.hasClass("active")){currentElement.removeClass("active");}else{currentElement.addClass("active");}});$(".goods__card__bag").click(()=>{let count=$(".count").text();count++;$(".count").text(count);});$(".goods__cards").slick({infinite:false,arrow:true,slidesToShow:4,slidesToScroll:1,});$(".popup-youtube").magnificPopup({disableOn:700,type:"iframe",mainClass:"mfp-fade",removalDelay:160,preloader:false,fixedContentPos:false,});});